-- Insert steps
INSERT INTO tutorials_steps (_order, _parent_id, id, step_number, title, estimated_minutes, content, alternative_content, alternative_label, tip, warning) VALUES
(1, 2, '6949ffa462ea7c3d5b09f7b5', 1, 'Set Up Your Runway Account', 5,
'{"root":{"type":"root","children":[{"type":"paragraph","children":[{"type":"text","text":"First, let''s get you set up with Runway and understand the credit system."}]},{"type":"heading","tag":"h4","children":[{"type":"text","text":"Create Your Account"}]},{"type":"list","listType":"number","children":[{"type":"listitem","children":[{"type":"text","text":"Go to runwayml.com and click \"Try Runway for Free\""}]},{"type":"listitem","children":[{"type":"text","text":"Sign up with Google or email"}]},{"type":"listitem","children":[{"type":"text","text":"You''ll get 125 free credits to start (enough for several test videos)"}]}]},{"type":"heading","tag":"h4","children":[{"type":"text","text":"Understanding Credits"}]},{"type":"paragraph","children":[{"type":"text","text":"Runway uses a credit system. Here''s how it works:"}]},{"type":"list","listType":"bullet","children":[{"type":"listitem","children":[{"type":"text","text":"Gen-3 Alpha:","bold":true},{"type":"text","text":" 10 credits per second of video"}]},{"type":"listitem","children":[{"type":"text","text":"Gen-3 Alpha Turbo:","bold":true},{"type":"text","text":" 5 credits per second (faster, slightly lower quality)"}]},{"type":"listitem","children":[{"type":"text","text":"A 5-second video costs 25-50 credits depending on the model"}]},{"type":"listitem","children":[{"type":"text","text":"Your free 125 credits = roughly 2-4 test videos"}]}]},{"type":"heading","tag":"h4","children":[{"type":"text","text":"Navigate to Gen-3"}]},{"type":"list","listType":"number","children":[{"type":"listitem","children":[{"type":"text","text":"From the Runway dashboard, look for \"Generate Videos\" in the left sidebar"}]},{"type":"listitem","children":[{"type":"text","text":"Select \"Gen-3 Alpha\" (or Gen-3 Alpha Turbo for faster results)"}]},{"type":"listitem","children":[{"type":"text","text":"You''ll see two input options: \"Text\" and \"Image + Text\""}]}]},{"type":"paragraph","children":[{"type":"text","text":"We''ll start with text-to-video, then level up to image-to-video for more control."}]}]}}',
'{"root":{"type":"root","children":[{"type":"paragraph","children":[{"type":"text","text":"If you prefer Pika 2.5:"}]},{"type":"list","listType":"number","children":[{"type":"listitem","children":[{"type":"text","text":"Go to pika.art and sign up"}]},{"type":"listitem","children":[{"type":"text","text":"Free tier gives you 80 credits per month"}]},{"type":"listitem","children":[{"type":"text","text":"Standard videos cost 10 credits each"}]},{"type":"listitem","children":[{"type":"text","text":"Click \"Create\" to access the video generator"}]}]},{"type":"paragraph","children":[{"type":"text","text":"Pika excels at creative effects. Try their \"Pikaffects\" feature for unique transformations like melting, exploding, or morphing objects."}]}]}}',
'Using Pika Instead',
'Start with Gen-3 Alpha Turbo to stretch your free credits further. The quality difference is subtle for most use cases.',
NULL),

(2, 2, '6949ffa462ea7c3d5b09f7b6', 2, 'Write Your First Video Prompt', 10,
'{"root":{"type":"root","children":[{"type":"paragraph","children":[{"type":"text","text":"Writing prompts for video is different from image generation. Motion and camera movement are key."}]},{"type":"heading","tag":"h4","children":[{"type":"text","text":"The Video Prompt Formula"}]},{"type":"paragraph","children":[{"type":"text","text":"A great video prompt has four components:"}]},{"type":"list","listType":"number","children":[{"type":"listitem","children":[{"type":"text","text":"Subject:","bold":true},{"type":"text","text":" What or who is in the scene"}]},{"type":"listitem","children":[{"type":"text","text":"Action/Motion:","bold":true},{"type":"text","text":" What movement is happening"}]},{"type":"listitem","children":[{"type":"text","text":"Environment:","bold":true},{"type":"text","text":" Where the scene takes place"}]},{"type":"listitem","children":[{"type":"text","text":"Style/Mood:","bold":true},{"type":"text","text":" Visual style, lighting, atmosphere"}]}]},{"type":"heading","tag":"h4","children":[{"type":"text","text":"Example Prompts to Try"}]},{"type":"paragraph","children":[{"type":"text","text":"Copy one of these prompts to get started:"}]},{"type":"paragraph","children":[{"type":"text","text":"Cinematic Nature:","bold":true}]},{"type":"code","children":[{"type":"text","text":"A majestic eagle soaring through a misty mountain valley at sunrise, slow motion, golden hour lighting, cinematic drone shot tracking the bird, 4K documentary style"}]},{"type":"paragraph","children":[{"type":"text","text":"Urban Scene:","bold":true}]},{"type":"code","children":[{"type":"text","text":"A woman in a red coat walking through a rainy Tokyo street at night, neon signs reflecting on wet pavement, camera slowly pushing forward, Blade Runner aesthetic"}]},{"type":"paragraph","children":[{"type":"text","text":"Product Shot:","bold":true}]},{"type":"code","children":[{"type":"text","text":"A coffee cup on a wooden table with steam rising, morning sunlight streaming through a window, camera slowly orbiting around the cup, cozy cafe atmosphere"}]},{"type":"heading","tag":"h4","children":[{"type":"text","text":"Camera Movement Keywords"}]},{"type":"paragraph","children":[{"type":"text","text":"Use these terms to control camera motion:"}]},{"type":"list","listType":"bullet","children":[{"type":"listitem","children":[{"type":"text","text":"\"tracking shot\"","bold":true},{"type":"text","text":" — camera follows the subject"}]},{"type":"listitem","children":[{"type":"text","text":"\"push in\" or \"dolly in\"","bold":true},{"type":"text","text":" — camera moves toward subject"}]},{"type":"listitem","children":[{"type":"text","text":"\"pull out\" or \"dolly out\"","bold":true},{"type":"text","text":" — camera moves away from subject"}]},{"type":"listitem","children":[{"type":"text","text":"\"orbit\" or \"arc\"","bold":true},{"type":"text","text":" — camera circles around subject"}]},{"type":"listitem","children":[{"type":"text","text":"\"static shot\"","bold":true},{"type":"text","text":" — camera stays still, only subject moves"}]},{"type":"listitem","children":[{"type":"text","text":"\"drone shot\"","bold":true},{"type":"text","text":" — aerial perspective"}]},{"type":"listitem","children":[{"type":"text","text":"\"slow motion\"","bold":true},{"type":"text","text":" — reduces perceived speed"}]}]}]}}',
NULL, NULL, NULL,
'Avoid prompts with text, logos, or specific brand names — AI video tools struggle with these. Also avoid complex multi-person scenes; start with single subjects.'),

(3, 2, '6949ffa462ea7c3d5b09f7b7', 3, 'Generate Your First Video', 5,
'{"root":{"type":"root","children":[{"type":"paragraph","children":[{"type":"text","text":"Now let''s create your first AI video. We''ll start with text-to-video."}]},{"type":"heading","tag":"h4","children":[{"type":"text","text":"Generate the Video"}]},{"type":"list","listType":"number","children":[{"type":"listitem","children":[{"type":"text","text":"In the Gen-3 interface, make sure \"Text\" mode is selected"}]},{"type":"listitem","children":[{"type":"text","text":"Paste your prompt in the text field"}]},{"type":"listitem","children":[{"type":"text","text":"Set duration to 5 seconds (conserves credits for testing)"}]},{"type":"listitem","children":[{"type":"text","text":"Leave other settings at default for now"}]},{"type":"listitem","children":[{"type":"text","text":"Click \"Generate\""}]}]},{"type":"heading","tag":"h4","children":[{"type":"text","text":"Wait for Processing"}]},{"type":"list","listType":"bullet","children":[{"type":"listitem","children":[{"type":"text","text":"Gen-3 Alpha: 60-90 seconds per video"}]},{"type":"listitem","children":[{"type":"text","text":"Gen-3 Alpha Turbo: 20-40 seconds per video"}]},{"type":"listitem","children":[{"type":"text","text":"You can queue multiple generations while waiting"}]}]},{"type":"heading","tag":"h4","children":[{"type":"text","text":"Evaluate Your Results"}]},{"type":"paragraph","children":[{"type":"text","text":"When reviewing your generated video, look for:"}]},{"type":"list","listType":"bullet","children":[{"type":"listitem","children":[{"type":"text","text":"Motion consistency:","bold":true},{"type":"text","text":" Does movement look natural?"}]},{"type":"listitem","children":[{"type":"text","text":"Subject integrity:","bold":true},{"type":"text","text":" Does the subject maintain its shape throughout?"}]},{"type":"listitem","children":[{"type":"text","text":"Prompt adherence:","bold":true},{"type":"text","text":" Did it follow your instructions?"}]},{"type":"listitem","children":[{"type":"text","text":"Artifacts:","bold":true},{"type":"text","text":" Any glitches, morphing, or unnatural elements?"}]}]},{"type":"paragraph","children":[{"type":"text","text":"Don''t expect perfection on the first try. Iteration is normal — even pros regenerate multiple times."}]}]}}',
NULL, NULL,
'If your first result isn''t great, try generating 2-3 more with the same prompt. AI video is probabilistic — you''ll often get varying quality from the same input.',
NULL),

(4, 2, '6949ffa462ea7c3d5b09f7b8', 4, 'Level Up with Image-to-Video', 7,
'{"root":{"type":"root","children":[{"type":"paragraph","children":[{"type":"text","text":"Image-to-video gives you much more control over the starting point. This is how professionals use AI video tools."}]},{"type":"heading","tag":"h4","children":[{"type":"text","text":"Why Use Image-to-Video?"}]},{"type":"list","listType":"bullet","children":[{"type":"listitem","children":[{"type":"text","text":"Consistent character/subject appearance across shots"}]},{"type":"listitem","children":[{"type":"text","text":"Precise control over the first frame composition"}]},{"type":"listitem","children":[{"type":"text","text":"Better results for specific products or branded content"}]},{"type":"listitem","children":[{"type":"text","text":"Animate existing artwork, photos, or AI-generated images"}]}]},{"type":"heading","tag":"h4","children":[{"type":"text","text":"Find or Create Your Source Image"}]},{"type":"paragraph","children":[{"type":"text","text":"You have several options:"}]},{"type":"list","listType":"number","children":[{"type":"listitem","children":[{"type":"text","text":"Use a stock photo","bold":true},{"type":"text","text":" from Unsplash or Pexels (free)"}]},{"type":"listitem","children":[{"type":"text","text":"Generate with AI","bold":true},{"type":"text","text":" using Midjourney, DALL-E, or Ideogram"}]},{"type":"listitem","children":[{"type":"text","text":"Use your own photo","bold":true},{"type":"text","text":" — landscapes and objects work best"}]}]},{"type":"heading","tag":"h4","children":[{"type":"text","text":"Generate Image-to-Video"}]},{"type":"list","listType":"number","children":[{"type":"listitem","children":[{"type":"text","text":"In Gen-3, switch to \"Image + Text\" mode"}]},{"type":"listitem","children":[{"type":"text","text":"Upload your source image"}]},{"type":"listitem","children":[{"type":"text","text":"Write a motion prompt (focus on what should MOVE, not what''s in the image)"}]},{"type":"listitem","children":[{"type":"text","text":"Click Generate"}]}]},{"type":"heading","tag":"h4","children":[{"type":"text","text":"Motion Prompt Examples"}]},{"type":"paragraph","children":[{"type":"text","text":"For a landscape image:"}]},{"type":"code","children":[{"type":"text","text":"Gentle wind moves through the grass, clouds slowly drift across the sky, camera slowly pushes forward"}]},{"type":"paragraph","children":[{"type":"text","text":"For a portrait:"}]},{"type":"code","children":[{"type":"text","text":"Subject slowly turns their head, subtle smile forms, hair gently moves, soft breathing motion"}]},{"type":"paragraph","children":[{"type":"text","text":"For a product shot:"}]},{"type":"code","children":[{"type":"text","text":"Camera slowly orbits around the product, reflections shift on the surface, dramatic lighting"}]}]}}',
'{"root":{"type":"root","children":[{"type":"paragraph","children":[{"type":"text","text":"Kling AI offers unique advantages:"}]},{"type":"list","listType":"bullet","children":[{"type":"listitem","children":[{"type":"text","text":"Generate videos up to 2 minutes long"}]},{"type":"listitem","children":[{"type":"text","text":"\"Elements\" feature maintains character consistency across shots"}]},{"type":"listitem","children":[{"type":"text","text":"More affordable at $4-10/month for entry plans"}]},{"type":"listitem","children":[{"type":"text","text":"Best for narrative content or character-driven videos"}]}]},{"type":"paragraph","children":[{"type":"text","text":"Access Kling at klingai.com — the interface is similar to Runway."}]}]}}',
'Using Kling AI for Longer Videos',
NULL,
'For image-to-video, your source image quality matters a lot. Use high-resolution images (at least 1024x1024) and avoid images with text or logos.'),

(5, 2, '6949ffa462ea7c3d5b09f7b9', 5, 'Export and Use Your Video', 3,
'{"root":{"type":"root","children":[{"type":"paragraph","children":[{"type":"text","text":"You''ve created your first AI video! Now let''s download it and understand your options for next steps."}]},{"type":"heading","tag":"h4","children":[{"type":"text","text":"Download Your Video"}]},{"type":"list","listType":"number","children":[{"type":"listitem","children":[{"type":"text","text":"Click on your finished video in Runway"}]},{"type":"listitem","children":[{"type":"text","text":"Click the download icon (arrow pointing down)"}]},{"type":"listitem","children":[{"type":"text","text":"Choose your quality (720p is free, 4K requires paid plan)"}]},{"type":"listitem","children":[{"type":"text","text":"Video downloads as MP4 — universally compatible"}]}]},{"type":"heading","tag":"h4","children":[{"type":"text","text":"Optional: Add Audio"}]},{"type":"paragraph","children":[{"type":"text","text":"AI videos are silent. To add music or sound effects:"}]},{"type":"list","listType":"bullet","children":[{"type":"listitem","children":[{"type":"text","text":"CapCut","bold":true},{"type":"text","text":" (free) — Browser-based, includes royalty-free music library"}]},{"type":"listitem","children":[{"type":"text","text":"DaVinci Resolve","bold":true},{"type":"text","text":" (free) — Professional-grade, more features"}]},{"type":"listitem","children":[{"type":"text","text":"ElevenLabs","bold":true},{"type":"text","text":" — Generate AI voiceovers to narrate your video"}]},{"type":"listitem","children":[{"type":"text","text":"Suno or Udio","bold":true},{"type":"text","text":" — Generate custom AI music to match your video mood"}]}]},{"type":"heading","tag":"h4","children":[{"type":"text","text":"Common Use Cases"}]},{"type":"list","listType":"bullet","children":[{"type":"listitem","children":[{"type":"text","text":"Social media content","bold":true},{"type":"text","text":" — Short clips for Instagram Reels, TikTok, YouTube Shorts"}]},{"type":"listitem","children":[{"type":"text","text":"B-roll footage","bold":true},{"type":"text","text":" — Fill gaps in video projects"}]},{"type":"listitem","children":[{"type":"text","text":"Product visualization","bold":true},{"type":"text","text":" — Showcase products before they exist"}]},{"type":"listitem","children":[{"type":"text","text":"Music videos","bold":true},{"type":"text","text":" — Create visuals for original music"}]},{"type":"listitem","children":[{"type":"text","text":"Presentation graphics","bold":true},{"type":"text","text":" — Add motion to slides and decks"}]}]},{"type":"paragraph","children":[{"type":"text","text":"Congratulations — you''ve just created your first AI-generated video!"}]}]}}',
NULL, NULL,
'Create a folder system for your AI videos: Drafts, Approved, Published. You''ll generate many iterations — staying organized saves time.',
NULL);

-- Insert next steps
INSERT INTO tutorials_next_steps (_order, _parent_id, id, title, description) VALUES
(1, 2, '6949ffa462ea7c3d5b09f7c1', 'Experiment with Different Styles', 'Try generating videos in different visual styles: anime, film noir, documentary, stop-motion, watercolor painting, etc.'),
(2, 2, '6949ffa462ea7c3d5b09f7c2', 'Create a Multi-Clip Sequence', 'Generate 3-5 related clips and edit them together in CapCut or DaVinci Resolve to create a complete scene.'),
(3, 2, '6949ffa462ea7c3d5b09f7c3', 'Try Pika''s Special Effects', 'Experiment with Pika''s "Pikaffects" to add creative transformations like melting, exploding, or morphing.'),
(4, 2, '6949ffa462ea7c3d5b09f7c4', 'Add AI Audio', 'Use ElevenLabs for voiceover or Suno/Udio for AI-generated music to create fully AI-produced videos.');
