UPDATE tutorials_steps SET
content = '{"root":{"type":"root","children":[{"type":"paragraph","children":[{"type":"text","text":"This is where the magic happens. We''ll connect Claude to analyze your notes and generate structured output."}]},{"type":"heading","tag":"h4","children":[{"type":"text","text":"Set Up Your Anthropic Account"}]},{"type":"paragraph","children":[{"type":"text","text":"Before connecting to Zapier, you need API access:"}]},{"type":"list","listType":"number","children":[{"type":"listitem","children":[{"type":"text","text":"Go to console.anthropic.com and sign in (or create an account)"}]},{"type":"listitem","children":[{"type":"text","text":"New accounts start on the \"Evaluation\" plan — go to Settings → Plans and select \"Build\""}]},{"type":"listitem","children":[{"type":"text","text":"Add credits ($5 minimum). Tip: You may be eligible for $5 free credits by verifying your phone number"}]},{"type":"listitem","children":[{"type":"text","text":"Click the key icon in the left navigation to access API Keys"}]},{"type":"listitem","children":[{"type":"text","text":"Click \"+ Create Key\", name it \"Zapier Second Brain\", and copy the key immediately (you won''t see it again!)"}]}]},{"type":"heading","tag":"h4","children":[{"type":"text","text":"Add Claude to Your Zap"}]},{"type":"list","listType":"number","children":[{"type":"listitem","children":[{"type":"text","text":"In Zapier, click the + to add an action"}]},{"type":"listitem","children":[{"type":"text","text":"Search for \"Anthropic (Claude)\""}]},{"type":"listitem","children":[{"type":"text","text":"Select \"Send Message\" as the action"}]},{"type":"listitem","children":[{"type":"text","text":"Click \"Sign in to Anthropic\" and paste your API key"}]}]},{"type":"heading","tag":"h4","children":[{"type":"text","text":"Configure the Prompt"}]},{"type":"paragraph","children":[{"type":"text","text":"In the \"User Message\" field, enter this prompt (and insert your Notion content field):"}]},{"type":"code","children":[{"type":"text","text":"Analyze this note and provide structured output:\n\nNOTE:\n{{Content from Notion}}\n\nRespond in EXACTLY this format (no other text):\n\nSUMMARY: [1-2 sentence summary of the main point]\n\nTAGS: [3-5 relevant single-word tags, comma-separated]\n\nACTION ITEMS: [Any tasks or to-dos mentioned, semicolon-separated. If none, write \"None\"]"}]},{"type":"paragraph","children":[{"type":"text","text":"Click the + button in the prompt field to insert your Notion \"Content\" field where it says {{Content from Notion}}."}]},{"type":"heading","tag":"h4","children":[{"type":"text","text":"Set Advanced Options"}]},{"type":"list","listType":"bullet","children":[{"type":"listitem","children":[{"type":"text","text":"Model:","bold":true},{"type":"text","text":" claude-haiku-4-5 (fast and affordable)"}]},{"type":"listitem","children":[{"type":"text","text":"Max Tokens:","bold":true},{"type":"text","text":" 500 (keeps responses concise)"}]},{"type":"listitem","children":[{"type":"text","text":"Temperature:","bold":true},{"type":"text","text":" 0.3 (more consistent output)"}]}]},{"type":"paragraph","children":[{"type":"text","text":"Click \"Test action\" to see Claude process your sample note."}]}]}}',
tip = 'Claude Haiku 4.5 is extremely affordable at $1 per million input tokens. Processing 1,000 short notes costs just a few cents.',
alternative_content = '{"root":{"type":"root","children":[{"type":"paragraph","children":[{"type":"text","text":"If you prefer ChatGPT/OpenAI:"}]},{"type":"list","listType":"number","children":[{"type":"listitem","children":[{"type":"text","text":"Get an API key from platform.openai.com"}]},{"type":"listitem","children":[{"type":"text","text":"Add $5+ in credits to enable API access"}]},{"type":"listitem","children":[{"type":"text","text":"In Zapier, search for \"ChatGPT\" or \"OpenAI\""}]},{"type":"listitem","children":[{"type":"text","text":"Select \"Conversation\" as the action (this is the recommended option)"}]},{"type":"listitem","children":[{"type":"text","text":"Use the same prompt as above"}]},{"type":"listitem","children":[{"type":"text","text":"Select a model from the dropdown (gpt-4o-mini offers good price/performance)"}]}]}]}}'
